describe('container', () => {
  it('applies default styles', () => {
    expect( // eslint-disable-next-line @typescript-eslint/no-explicit-any
    (0, _container.default)(100).style._values).toMatchObject({
      background: 'white',
      'border-radius': '50px',
      display: 'inline-block',
      height: '100px',
      margin: '0px',
      overflow: 'hidden',
      padding: '0px',
      width: '100px'
    });
  });
  it('overrides with supplied styles', () => {
    expect( // eslint-disable-next-line @typescript-eslint/no-explicit-any
    (0, _container.default)(50, 'black', '', {
      display: 'block'
    }).style._values).toMatchObject({
      background: 'black',
      'border-radius': '25px',
      display: 'block',
      height: '50px',
      margin: '0px',
      overflow: 'hidden',
      padding: '0px',
      width: '50px'
    });
  });
  it('applies the specified className', () => {
    expect((0, _container.default)(100, 'blue', 'testClass').className).toEqual('testClass');
  });
});